// src/components/Home.js
import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

const Home = () => {
  const [employees, setEmployees] = useState([]);
  const [searchId, setSearchId] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchEmployees();
  }, []);

  const fetchEmployees = async () => {
    setLoading(true);
    setError(null);
    try {
      const response = await axios.get(
        "https://669b3f09276e45187d34eb4e.mockapi.io/api/v1/employee"
      );
      setEmployees(response.data);
    } catch (error) {
      setError("Error fetching employees");
      console.error("Error fetching employees", error);
    } finally {
      setLoading(false);
    }
  };

  const handleSearch = async () => {
    if (searchId) {
      setLoading(true);
      setError(null);
      try {
        const response = await axios.get(
          `https://669b3f09276e45187d34eb4e.mockapi.io/api/v1/employee/${searchId}`
        );
        setEmployees([response.data]);
      } catch (error) {
        setError("Error searching employee");
        console.error("Error searching employee", error);
      } finally {
        setLoading(false);
      }
    } else {
      fetchEmployees();
    }
  };

  const handleDelete = async (id) => {
    if (window.confirm("Are you sure you want to delete this employee?")) {
      setLoading(true);
      setError(null);
      try {
        await axios.delete(
          `https://669b3f09276e45187d34eb4e.mockapi.io/api/v1/employee/${id}`
        );
        fetchEmployees();
      } catch (error) {
        setError("Error deleting employee");
        console.error("Error deleting employee", error);
      } finally {
        setLoading(false);
      }
    }
  };

  return (
    <div className="container mt-4">
      <h1>Employee List</h1>
      <div className="mb-3">
        <input
          type="text"
          className="form-control"
          placeholder="Search by ID"
          value={searchId}
          onChange={(e) => setSearchId(e.target.value)}
        />
        <button className="btn btn-primary mt-2" onClick={handleSearch}>
          Search
        </button>
        <Link to="/add">
          <button className="btn btn-success ms-2 mt-2">Add Employee</button>
        </Link>
      </div>
      {loading && <p>Loading...</p>}
      {error && <p className="text-danger">{error}</p>}
      <table className="table">
        <thead>
          <tr>
            <th>Avatar</th>
            <th>Name</th>
            <th>Email</th>
            <th>Mobile</th>
            <th>Country</th>
            <th>State</th>
            <th>District</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {employees.length > 0 ? (
            employees.map((employee) => (
              <tr key={employee.id}>
                <td>
                  <img
                    src={employee.avatar}
                    alt={employee.name}
                    className="img-fluid rounded-circle"
                    style={{ width: "50px", height: "50px" }}
                  />
                </td>
                <td>{employee.name}</td>
                <td>{employee.emailId}</td>
                <td>{employee.mobile}</td>
                <td>{employee.country}</td>
                <td>{employee.state}</td>
                <td>{employee.district}</td>
                <td>
                  <Link to={`/edit/${employee.id}`}>
                    <button className="btn btn-primary me-2">Edit</button>
                  </Link>
                  <button
                    className="btn btn-danger"
                    onClick={() => handleDelete(employee.id)}
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="8">No employees found</td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
};

export default Home;
