// src/components/AddEditEmployee.js
import React, { useState, useEffect } from "react";
import axios from "axios";
import { useParams, useNavigate } from "react-router-dom";

const AddEditEmployee = () => {
  const [employee, setEmployee] = useState({
    name: "",
    email: "",
    mobile: "",
    country: "",
    state: "",
    district: "",
  });
  const [countries, setCountries] = useState([]);
  const { id } = useParams();
  const navigate = useNavigate();

  useEffect(() => {
    if (id) {
      fetchEmployee(id);
    }
    fetchCountries();
  }, [id]);

  const fetchEmployee = async (id) => {
    try {
      const response = await axios.get(
        `https://669b3f09276e45187d34eb4e.mockapi.io/api/v1/employee/${id}`
      );
      setEmployee(response.data);
    } catch (error) {
      console.error("Error fetching employee", error);
    }
  };

  const fetchCountries = async () => {
    try {
      const response = await axios.get(
        "https://669b3f09276e45187d34eb4e.mockapi.io/api/v1/country"
      );
      setCountries(response.data);
    } catch (error) {
      console.error("Error fetching countries", error);
    }
  };

  const handleChange = (e) => {
    setEmployee({ ...employee, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (id) {
        await axios.put(
          `https://669b3f09276e45187d34eb4e.mockapi.io/api/v1/employee/${id}`,
          employee
        );
      } else {
        await axios.post(
          "https://669b3f09276e45187d34eb4e.mockapi.io/api/v1/employee",
          employee
        );
      }
      navigate("/");
    } catch (error) {
      console.error("Error saving employee", error);
    }
  };

  return (
    <div>
      <h1>{id ? "Edit Employee" : "Add Employee"}</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="name"
          value={employee.name}
          onChange={handleChange}
          placeholder="Name"
        />
        <input
          type="email"
          name="email"
          value={employee.email}
          onChange={handleChange}
          placeholder="Email"
        />
        <input
          type="text"
          name="mobile"
          value={employee.mobile}
          onChange={handleChange}
          placeholder="Mobile"
        />
        <select name="country" value={employee.country} onChange={handleChange}>
          {countries.map((country) => (
            <option key={country.code} value={country.code}>
              {country.name}
            </option>
          ))}
        </select>
        <input
          type="text"
          name="state"
          value={employee.state}
          onChange={handleChange}
          placeholder="State"
        />
        <input
          type="text"
          name="district"
          value={employee.district}
          onChange={handleChange}
          placeholder="District"
        />
        <button type="submit">{id ? "Update" : "Add"}</button>
      </form>
    </div>
  );
};

export default AddEditEmployee;
