// src/components/EmployeeDetails.js
import React, { useState, useEffect } from "react";
import axios from "axios";
import { useParams, Link } from "react-router-dom";

const EmployeeDetails = () => {
  const { id } = useParams();
  const [employee, setEmployee] = useState(null);

  useEffect(() => {
    fetchEmployeeDetails();
  }, [id]);

  const fetchEmployeeDetails = async () => {
    try {
      const response = await axios.get(
        `https://669b3f09276e45187d34eb4e.mockapi.io/api/v1/employee/${id}`
      );
      setEmployee(response.data);
    } catch (error) {
      console.error("Error fetching employee details", error);
    }
  };

  return (
    <div className="container mt-4">
      {employee ? (
        <div>
          <div className="d-flex align-items-center mb-4">
            <img
              src={employee.avatar}
              alt={employee.name}
              className="img-fluid rounded-circle me-3"
              style={{ width: '100px', height: '100px' }}
            />
            <div>
              <h1 className="mb-3">{employee.name}</h1>
              <p><strong>Email:</strong> {employee.emailId}</p>
              <p><strong>Mobile:</strong> {employee.mobile}</p>
              <p><strong>Country:</strong> {employee.country}</p>
              <p><strong>State:</strong> {employee.state}</p>
              <p><strong>District:</strong> {employee.district}</p>
            </div>
          </div>
          <Link to={`/edit/${employee.id}`}>
            <button className="btn btn-primary">Edit</button>
          </Link>
        </div>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
};

export default EmployeeDetails;
