import React, { useState, useEffect } from "react";
import axios from "axios";
import { useParams, useNavigate } from "react-router-dom";

const AddEditEmployee = () => {
  const [employee, setEmployee] = useState({
    name: "",
    email: "",
    mobile: "",
    country: "",
    state: "",
    district: "",
  });
  const [countries, setCountries] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const { id } = useParams();
  const navigate = useNavigate();

  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        if (id) {
          const { data } = await axios.get(
            `https://669b3f09276e45187d34eb4e.mockapi.io/api/v1/employee/${id}`
          );
          setEmployee(data);
        }
        const { data: countriesData } = await axios.get(
          "https://669b3f09276e45187d34eb4e.mockapi.io/api/v1/country"
        );
        setCountries(countriesData);
      } catch {
        setError("Error fetching data");
      } finally {
        setLoading(false);
      }
    };
    fetchData();
  }, [id]);

  const handleChange = (e) => setEmployee({ ...employee, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (id) {
        await axios.put(
          `https://669b3f09276e45187d34eb4e.mockapi.io/api/v1/employee/${id}`,
          employee
        );
      } else {
        await axios.post(
          "https://669b3f09276e45187d34eb4e.mockapi.io/api/v1/employee",
          employee
        );
      }
      navigate("/");
    } catch {
      setError("Error saving employee");
    }
  };

  if (loading) return <div>Loading...</div>;

  return (
    <div className="container mt-4">
      <h1 className="mb-4">{id ? "Edit Employee" : "Add Employee"}</h1>
      {error && <div className="alert alert-danger">{error}</div>}
      <form onSubmit={handleSubmit}>
        {["name", "email", "mobile", "state", "district"].map((field) => (
          <div key={field} className="mb-3">
            <label htmlFor={field} className="form-label">{field.charAt(0).toUpperCase() + field.slice(1)}</label>
            <input
              type={field === "email" ? "email" : "text"}
              id={field}
              name={field}
              className="form-control"
              value={employee[field]}
              onChange={handleChange}
              placeholder={field.charAt(0).toUpperCase() + field.slice(1)}
              required
            />
          </div>
        ))}
        <div className="mb-3">
          <label htmlFor="country" className="form-label">Country</label>
          <select
            id="country"
            name="country"
            className="form-select"
            value={employee.country}
            onChange={handleChange}
            required
          >
            <option value="">Select a country</option>
            {countries.map((country) => (
              <option key={country.id} value={country.id}>{country.country}</option>
            ))}
          </select>
        </div>
        <button type="submit" className="btn btn-primary">
          {id ? "Update" : "Add"}
        </button>
      </form>
    </div>
  );
};

export default AddEditEmployee;
