import React, { useState, useEffect } from "react";
import { useParams, Link } from "react-router-dom";
import axios from "axios";
import "bootstrap/dist/css/bootstrap.min.css";

import { Button, Modal } from "react-bootstrap";

const EmployeeDetails = () => {
  const { id } = useParams();
  const [employee, setEmployee] = useState(null);

  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  useEffect(() => {
    axios
      .get(`https://669b3f09276e45187d34eb4e.mockapi.io/api/v1/employee/${id}`)
      .then((response) => {
        setEmployee(response.data);
      })
      .catch((error) => {
        console.error(
          "There was an error fetching the employee details!",
          error
        );
      });
  }, [id]);

  if (!employee) {
    return <div>No employee details found.</div>;
  }

  return (
    <>
      <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>Modal Heading</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <div className="container mt-4">
            <h1 className="mb-4">Employee Details</h1>
            <div className="card">
              <div className="card-body">
                <div className="row">
                  <div className="col-md-4">
                    <h5 className="card-title">Name: {employee.name}</h5>
                    <p className="card-text">
                      <strong>Email:</strong> {employee.emailId}
                    </p>
                    <p className="card-text">
                      <strong>Mobile:</strong> {employee.mobile}
                    </p>
                    <p className="card-text">
                      <strong>Country:</strong> {employee.country}
                    </p>
                    <p className="card-text">
                      <strong>State:</strong> {employee.state}
                    </p>
                    <p className="card-text">
                      <strong>District:</strong> {employee.district}
                    </p>
                    <Link
                      to={`/edit/${employee.id}`}
                      className="btn btn-primary"
                    >
                      <i className="fas fa-edit"></i> Edit
                    </Link>
                  </div>
                  <div className="col-md-4">
                    {employee.avatar && (
                      <img
                        src={employee.avatar}
                        alt="Employee Avatar"
                        className="img-fluid"
                      />
                    )}
                  </div>
                </div>
              </div>
            </div>
          </div>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
          {/* <Button variant="primary" onClick={handleClose}>
                        Save Changes
                    </Button> */}
        </Modal.Footer>
      </Modal>
    </>
  );
};

export default EmployeeDetails;
