// EmployeeDetailsModal.js
import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import { Modal, Button } from "react-bootstrap";

const EmployeeDetailsModal = ({ showModal, handleClose }) => {
  const { id } = useParams();
  const [employee, setEmployee] = useState(null);

  useEffect(() => {
    if (id) {
      axios
        .get(`https://669b3f09276e45187d34eb4e.mockapi.io/api/v1/employee/${id}`)
        .then((response) => {
          setEmployee(response.data);
        })
        .catch((error) => {
          console.error("There was an error fetching the employee details!", error);
        });
    }
  }, [id]);

  return (
    <Modal show={showModal} onHide={handleClose}>
      <Modal.Header closeButton>
        <Modal.Title>Employee Details</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        {employee ? (
          <div>
            <h5>Name: {employee.name}</h5>
            <p>Email: {employee.emailId}</p>
            <p>Mobile: {employee.mobile}</p>
            <p>Country: {employee.country}</p>
            <p>State: {employee.state}</p>
            <p>District: {employee.district}</p>
            {employee.avatar && (
              <img
                src={employee.avatar}
                alt="Employee Avatar"
                className="img-fluid"
              />
            )}
          </div>
        ) : (
          <div>Loading...</div>
        )}
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={handleClose}>
          Close
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

export default EmployeeDetailsModal;
